        <br />
        <div className="block">
          {inputType === "Plot Size" ? (
            <input />
          ) : (
            <label style={{ position: "relative" }}>
              Plot Size
              <input
                type="number"
                style={{
                  width: 110,
                  marginLeft: 15,
                  paddingLeft: 5,
                  marginRight: 5,
                }}
              />
              <span style={{ position: "absolute", top: 1 }}>Sqft</span>
            </label>
          )}
        </div>
      </div>
    </div>
  );
};
