import React from "react";
import styled from "styled-components";

import Package from "../../Package";
import "./style.css";
const cnst_bg = require("../../../assets/cnst_bg6.jpg").default;

const Input = styled.input`
  width: 90%;
  height: 50px;
  padding: 5px;
  margin-top: 30px;
  border: 0;
  border-bottom: 1px solid;
  outline: none;
  font-size: 20px;
  background-color: #4a148c;
  color: white;
  border-radius: 4px;

  ::-webkit-inner-spin-button,
  ::-webkit-outer-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  &:-webkit-autofill,
  &:-webkit-autofill:hover,
  &:-webkit-autofill:focus,
  &:-webkit-autofill:active {
    -webkit-transition: "color 9999s ease-out, background-color 9999s ease-out";
    -webkit-transition-delay: 9999s;
  }
`;

const Form = styled.div`
  height: 450px;
  max-width: 400px;
  min-width: 400px;
  background-color: #4a148c;
  margin-top: 5%;
  opacity: 0.8;
  border-radius: 10px;

  @media screen and (max-width: 810px) {
    margin-top: 15%;
  }
`;

const Main = styled.div`
  height: 70vh;
  width: 100%;
  text-align: center;
  justify-content: space-around;
  color: white;
  background-image: url(${cnst_bg});
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
`;

const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

const Construction = () => {
  return (
    <>
      <div className="row">
        <Main className="row">
          <div className="col" style={{ maxWidth: "50%", minWidth: 400 }}>
            <p
              style={{
                fontSize: 45,
                fontWeight: "bold",
                marginTop: "10%",
              }}
            >
              We build quality Homes
            </p>
            <p style={{ fontSize: 25 }}>100% Hassle-Free and managed by us!</p>
          </div>
          <Form className="col border-primary">
            <Input placeholder="Your name" type="text" />
            <Input placeholder="Your contact number" type="number" />
            <Input placeholder="Your email" type="email" />
            <Input placeholder="Your city" type="text" />
            <button
              style={{ flex: 1, margin: 20 }}
              type="submit"
              class="btn btn-primary btn-lg btn-block"
            >
              Submit
            </button>
          </Form>
        </Main>
        <Div>
          <h1>Our Packages</h1>
          <div className="row justify-content-center">
            <Package
              title="STANDARD"
              area="1700/Sqft"
              text1="Structure – branded and high quality cement, steel, concrete, bricks"
              text2="Bare shell – W/O Interiors"
            />
            <Package
              title="PREMIUM"
              area="2300/Sqft"
              text1="Structure – branded and high quality cement, steel, concrete, bricks"
              text2="With Interior"
            />
            <Package
              title="SUPER PREMIUM"
              area="1700/Sqft"
              text1="Structure – branded and high quality cement, steel, concrete, bricks"
              text2="With Interior"
            />
            <Package
              title="LUXURY"
              area="1700/Sqft"
              text1="Structure – branded and high quality cement, steel, concrete, bricks"
              text2="With Interior"
            />
          </div>
        </Div>
      </div>
    </>
  );
};

export default Construction;

// echo "# construction" >> README.md
