import React, { useState } from "react";
import Button from "../../Button";
import Color from "../../../constants/Color";
import { SmallImage, LargeImage } from "./GalleryElements";
import { ImageData } from "./imageData";
import SimpleImageSlider from "react-simple-image-slider";
import "bootstrap/dist/css/bootstrap.css";
import "./style.css";

const Modal = ({ handleClose, show, children }) => {
  const showHideClassName = show ? "modal d-block" : "modal d-none";

  return (
    <div className={showHideClassName}>
      <a
        className="row"
        onClick={handleClose}
        style={{
          color: "white",
          marginRight: "10%",
          marginTop: 50,
          fontSize: 35,
          cursor: "pointer",
          textDecoration: "none",
          justifyContent: "right",
        }}
      >
        &#x2715;
      </a>
      <div
        className="modal-container"
        style={{
          position: "absolute",
          width: "60%",
          backgroundColor: "#ffffff",
          left: "25%",
          top: "10%",
        }}
      >
        {children}
      </div>
    </div>
  );
};

const Image = (props) => {
  const [modal, setModal] = useState(false);
  const [imgUrl, setImgUrl] = useState("");

  const modalClose = () => {
    setModal(false);
  };
  const a = ImageData.filter((image) => image.theme === props.theme);
  const b = a.filter((image) => image.type === props.type);

  return (
    <div>
      <h3>{props.theme}</h3>
      <div
        className="row"
        style={{
          // margin: 10,
          justifyContent: "flex-start",
        }}
      >
        {b.map((image, i) => (
          <div
            className="col-4"
            key={i}
            style={{
              minWidth: 300,
            }}
          >
            {props.living &&
              image.type === "livingRoom" &&
              image.theme === props.theme && (
                <SmallImage
                  key={i}
                  src={image.url}
                  onClick={() => {
                    setImgUrl(image.type);
                    setModal(true);
                  }}
                />
              )}

            {props.bed &&
              image.type === "bedRoom" &&
              image.theme === props.theme && (
                <SmallImage
                  key={i}
                  src={image.url}
                  onClick={() => {
                    setImgUrl(image.type);
                    setModal(true);
                  }}
                />
              )}
            {props.kitchen &&
              image.type === "kitchen" &&
              image.theme === props.theme && (
                <SmallImage
                  src={image.url}
                  key={i}
                  onClick={() => {
                    setImgUrl(image.type);
                    setModal(true);
                  }}
                />
              )}
          </div>
        ))}
        {modal && (
          <div className="App" style={{ textAlign: "center" }}>
            <Modal show={modal} handleClose={modalClose}>
              {/* <img src={imgUrl} style={{ width: "100%", height: "100%" }} /> */}

              <SimpleImageSlider
                width={"100%"}
                height={800}
                images={b.filter((image) => image.type === imgUrl)}
                showBullets={true}
                showNavs={true}
              />
            </Modal>
          </div>
        )}
      </div>
    </div>
  );
};

const Gallery = (props) => {
  const [living, setLiving] = useState(false);
  const [bed, setBed] = useState(false);
  const [kitchen, setKitchen] = useState(false);
  const [modern, setModern] = useState(
    props.location.param1 === "Modern" && true
  );
  const [contemporary, setContemporary] = useState(
    props.location.param1 === "Contemporary" && true
  );
  const [minimalsitic, setMinimalsitic] = useState(
    props.location.param1 === "Minimalsitic" && true
  );
  const [vintage, setVintage] = useState(
    props.location.param1 === "Vintage" && true
  );
  const [european, setEuropean] = useState(
    props.location.param1 === "European" && true
  );
  const [type, setType] = useState("living");

  return (
    <div
      className="row"
      style={{
        textAlign: "center",
        justifyContent: "center",
        margin: 0,
        padding: 0,
      }}
    >
      {console.log(props.location.param1)}
      <div
        className="row border"
        style={{
          justifyContent: "center",
          textAlign: "center",
          margin: 20,
          maxWidth: 600,
        }}
      >
        <Button
          text="Living rooms"
          color={Color.primary}
          type={living}
          onPress={() => {
            setLiving(true);
            setBed(false);
            setKitchen(false);
            setType("livingRoom");
          }}
        />
        <Button
          text="Bedroom"
          color={Color.primary}
          type={bed}
          onPress={() => {
            setLiving(false);
            setBed(true);
            setKitchen(false);
            setType("bedRoom");
          }}
        />
        <Button
          text="Kitchen"
          color={Color.primary}
          type={kitchen}
          onPress={() => {
            setLiving(false);
            setBed(false);
            setKitchen(true);
            setType("kitchen");
          }}
        />
      </div>

      <div
        className="row"
        style={{
          backgroundColor: "#F8F8F6",
          justifyContent: "center",
          textAlign: "center",
        }}
      >
        <div
          className="border col-2 "
          style={{ minWidth: 300, margin: 15, textAlign: "center" }}
        >
          <h4>Filter by Themes</h4>
          <div
            style={{
              textAlign: "center",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button
              text="Modern"
              color={Color.primary}
              type={modern}
              onPress={() => {
                setModern(true);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Contemporary"
              color={Color.primary}
              type={contemporary}
              onPress={() => {
                setModern(false);
                setContemporary(true);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Minimalsitic"
              color={Color.primary}
              type={minimalsitic}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(true);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Vintage"
              color={Color.primary}
              type={vintage}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(true);
                setEuropean(false);
              }}
            />
            <Button
              text="European"
              color={Color.primary}
              type={european}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(true);
              }}
            />
          </div>
        </div>
        <div className="col border" style={{ maxWidth: 1000, margin: 15 }}>
          {modern && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Modern"
              type={type}
            />
          )}
          {contemporary && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Contemporary"
              type={type}
            />
          )}
          {minimalsitic && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Minimalsitic"
              type={type}
            />
          )}
          {vintage && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Vintage"
              type={type}
            />
          )}
          {european && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="European"
              type={type}
            />
          )}

          {/* {image && (
            <SimpleImageSlider
              width={896}
              height={504}
              images={imgUrl}
              showBullets={true}
              showNavs={true}
            />
          )} */}
          {/* <SimpleImageSlider
            width={896}
            height={504}
            images={ImageData.filter((image) => image.type === "kitchen")}
            showBullets={true}
            showNavs={true}
          /> */}
        </div>
      </div>
    </div>
  );
};

export default Gallery;
