import { NavLink as Link } from "react-router-dom";
import styled from "styled-components";
import { GiHomeGarage } from "react-icons/gi";

const menu = require("../assets/menu-icon.png").default;

export const NavLink = styled(Link)`
  color: #fff;
  align-items: center;
  text-decoration: none;
  padding: 0 1rem;
  height: 100%;
  &.active {
    color: #15cdfc;
  }
`;

const Nav = styled.nav`
  background: #4a148c;
  flex: 1;
`;

const NavBar = () => {
  return (
    <Nav className="navbar navbar-expand-lg  sticky-top">
      <div className="container">
        <NavLink to="/" className="navbar-brand">
          <h1>{/* <GiHomeGarage /> */}Home</h1>
        </NavLink>
        <button
          className="navbar-toggler "
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
          style={{ color: "white" }}
        >
          <span class="navbar-toggler-icon"></span>
          {/* <img style={{ height: 20 }} src={menu} /> */}
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <NavLink to="/construction" activeStyle>
                Construction
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink to="/interior-design" activeStyle>
                Interior Design
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink to="/renovation" activeStyle>
                Renovation
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink to="/about" activeStyle>
                About us
              </NavLink>
            </li>
          </ul>
        </div>
      </div>
    </Nav>
  );
};

export default NavBar;
