import React, { useState, Component } from "react";
import styled from "styled-components";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";
export const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

export class ConstCostEstimator extends React.Component {
  constructor() {
    super();

    this.state = {
      size: "Plot Size",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

    this.state = {
      checked: false,
    };
  }
  toggleChange = () => {
    this.setState({
      isChecked: !this.state.isChecked,
    });
  };
  handleChange(event) {
    this.setState({
      size: event.target.value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    alert(`You chose the ${this.state.size} pizza.`);
  }

  render() {
    return (
      <div
        className="border row"
        style={{
          marginTop: 50,
          backgroundColor: "#F8F8F6",
          width: 450,
          borderRadius: 5,
          textAlign: "center",
          justifyContent: "center",
        }}
      >
        <h4 style={{ marginTop: 15 }}>Construction Cost Estimator</h4>
        <div
          className="border"
          style={{
            justifyContent: "flex-start",
            textAlign: "start",
            width: 250,
          }}
          onSubmit={this.handleSubmit}
        >
          <input
            className="form-check-input mt-1"
            type="radio"
            value="Plot Size"
            style={{ marginRight: 15 }}
            checked={this.state.size === "Plot Size"}
            onChange={this.handleChange}
          />
          <label>Plot Size</label>

          <input
            className="form-check-input mt-1"
            type="radio"
            value="Site Area"
            style={{ margin: 20 }}
            checked={this.state.size === "Site Area"}
            onChange={this.handleChange}
          />
          <label>Site Area</label>

          <br />
          <div
            className="block row"
            style={{ justifyContent: "start", textAlign: "start" }}
          >
            {this.state.size === "Plot Size" && (
              <select
                style={{
                  width: 210,
                  height: 30,
                  marginLeft: 15,
                  paddingLeft: 5,
                  marginRight: 5,
                }}
              >
                <option selected>Select</option>
                <option value="1">30 X 40</option>
                <option value="2">30 X 50</option>
                <option value="3">40 X 60</option>
              </select>
            )}
            {this.state.size === "Site Area" && (
              <label style={{ position: "relative" }}>
                Plot Size
                <input
                  type="number"
                  style={{
                    width: 110,
                    marginLeft: 15,
                    paddingLeft: 5,
                    marginRight: 5,
                  }}
                />
                <span style={{ position: "absolute", top: 1 }}>Sqft</span>
              </label>
            )}

            <div className="block">
              <label style={{ margin: 5 }}>Floor</label>
              <select
                type="text"
                style={{
                  width: 155,
                  marginLeft: 10,
                  marginTop: 20,
                  height: 30,
                }}
              >
                <option disabled>Select</option>
                <option value="1">Ground</option>
                <option value="2">Ground + 1</option>
                <option value="3">Ground + 2</option>
                <option value="3">Ground + 3</option>
              </select>
            </div>
            <div>
              <input
                style={{ margin: 5 }}
                type="checkbox"
                defaultChecked={this.state.isChecked}
                onChange={this.toggleChange}
              />
              <label style={{ margin: 5 }}>Car Parking</label>
              {this.state.checked && (
                <select style={{ width: 55, marginLeft: 10 }}>
                  <option selected value="1">
                    1
                  </option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                </select>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

