import React, { useState } from "react";
import Button from "../../Button";
import Color from "../../../constants/Color";
import { SmallImage, LargeImage } from "./GalleryElements";
import { ImageData } from "./imageData";
import SimpleImageSlider from "react-simple-image-slider";

const Gallery = () => {
  const [living, setLiving] = useState(true);
  const [bed, setBed] = useState(false);
  const [kitchen, setKitchen] = useState(false);
  const [modern, setModern] = useState(true);
  const [contemporary, setContemporary] = useState(false);
  const [minimalsitic, setMinimalsitic] = useState(false);
  const [vintage, setVintage] = useState(false);
  const [european, setEuropean] = useState(false);

  const [image, setImage] = useState(true);
  const [imgUrl, setImgUrl] = useState("");

  return (
    <div
      className="row"
      style={{ textAlign: "center", justifyContent: "center" }}
    >
      <div
        className="row border"
        style={{
          justifyContent: "center",
          textAlign: "center",
          margin: 20,
          maxWidth: 600,
        }}
      >
        <Button
          text="Living rooms"
          color={Color.primary}
          type={living}
          onPress={() => {
            setLiving(true);
            setBed(false);
            setKitchen(false);
          }}
        />
        <Button
          text="Bedroom"
          color={Color.primary}
          type={bed}
          onPress={() => {
            setLiving(false);
            setBed(true);
            setKitchen(false);
          }}
        />
        <Button
          text="Kitchen"
          color={Color.primary}
          type={kitchen}
          onPress={() => {
            setLiving(false);
            setBed(false);
            setKitchen(true);
          }}
        />
      </div>

      <div
        className="row"
        style={{
          backgroundColor: "#F8F8F6",
          justifyContent: "center",
          textAlign: "center",
        }}
      >
        <div
          className="border col-2 "
          style={{ minWidth: 300, margin: 15, textAlign: "center" }}
        >
          <h4>Filter by Themes</h4>
          <div
            style={{
              textAlign: "center",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button
              text="Modern"
              color={Color.primary}
              type={modern}
              onPress={() => {
                setModern(true);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Contemporary"
              color={Color.primary}
              type={contemporary}
              onPress={() => {
                setModern(false);
                setContemporary(true);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Minimalsitic"
              color={Color.primary}
              type={minimalsitic}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(true);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Vintage"
              color={Color.primary}
              type={vintage}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(true);
                setEuropean(false);
              }}
            />
            <Button
              text="European"
              color={Color.primary}
              type={european}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(true);
              }}
            />
          </div>
        </div>
        <div className="col border" style={{ maxWidth: 1000, margin: 15 }}>
          <h3>Modern</h3>
          <div>
            {ImageData.map((image, i) => (
              <div className="row" key={i}>
                <div className="col">
                  {living && image.type === "livingRoom" && (
                    <SmallImage key={i} src={image.url} />
                  )}
                </div>

                {bed && image.type === "bedRoom" && (
                  <SmallImage src={image.url} />
                )}
                {kitchen && image.type === "kitchen" && (
                  // image.kitchenRoom.url.map((img, index) => (
                  <SmallImage
                    src={image.url}
                    key={i}
                    onClick={() => {
                      // setImage(false);
                      // setImgUrl(img);
                    }}
                  />
                )}
              </div>
            ))}
          </div>

          {/* {image && (
            <SimpleImageSlider
              width={896}
              height={504}
              images={imgUrl}
              showBullets={true}
              showNavs={true}
            />
          )} */}
          {/* <SimpleImageSlider
            width={896}
            height={504}
            images={ImageData.filter((image) => image.type === "kitchen")}
            showBullets={true}
            showNavs={true}
          /> */}
        </div>
      </div>
    </div>
  );
};

