import React, { useState } from "react";
import Button from "../../Button";
import Color from "../../../constants/Color";
import { SmallImage, LargeImage } from "./GalleryElements";
import { ImageData } from "./imageData";
import SimpleImageSlider from "react-simple-image-slider";

const Img = (props) => {
  return <img style={{ width: 900, position: "" }} src={props.url} />;
};

const Image = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [imgUrl, setImgUrl] = useState("");
  const handleImageShow = () => {
    setIsOpen(!isOpen);
  };

  const a = ImageData.filter((image) => image.theme === props.theme);
  const b = a.filter((image) => image.type === props.type);
  return (
    <div>
      <h3>{props.theme}</h3>
      <div
        className="row"
        style={{
          // margin: 10,
          justifyContent: "flex-start",
        }}
      >
        {b.map((image, i) => (
          <div
            className="col-4"
            key={i}
            style={{
              minWidth: 300,
            }}
          >
            {props.living &&
              image.type === "livingRoom" &&
              image.theme === props.theme && (
                <SmallImage
                  key={i}
                  src={image.url}
                  onClick={() => {
                    setImgUrl(image.url);
                    setIsOpen(!isOpen);
                  }}
                />
              )}

            {props.bed &&
              image.type === "bedRoom" &&
              image.theme === props.theme && (
                <SmallImage
                  key={i}
                  src={image.url}
                  onClick={() => {
                    setImgUrl(image.url);
                    setIsOpen(!isOpen);
                  }}
                />
              )}
            {props.kitchen &&
              image.type === "kitchen" &&
              image.theme === props.theme && (
                <SmallImage src={image.url} key={i} />
              )}
          </div>
        ))}

        {"#myModal".on("shown.bs.modal", function () {
          "#myInput".trigger("focus");
        })}
        <button
          type="button"
          class="btn btn-primary"
          data-toggle="modal"
          data-target="#exampleModal"
        >
          Launch demo modal
        </button>
        <div
          class="modal fade"
          id="exampleModal"
          tabindex="-1"
          role="dialog"
          aria-labelledby="exampleModalLabel"
          aria-hidden="true"
        >
          <div class="modal-dialog" role="document">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title" id="exampleModalLabel">
                  Modal title
                </h5>
                <button
                  type="button"
                  class="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div class="modal-body">...</div>
              <div class="modal-footer">
                <button
                  type="button"
                  class="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
                <button type="button" class="btn btn-primary">
                  Save changes
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const Gallery = () => {
  const [living, setLiving] = useState(true);
  const [bed, setBed] = useState(false);
  const [kitchen, setKitchen] = useState(false);
  const [modern, setModern] = useState(true);
  const [contemporary, setContemporary] = useState(false);
  const [minimalsitic, setMinimalsitic] = useState(false);
  const [vintage, setVintage] = useState(false);
  const [european, setEuropean] = useState(false);
  const [type, setType] = useState("living");

  return (
    <div
      className="row"
      style={{
        textAlign: "center",
        justifyContent: "center",
        margin: 0,
        padding: 0,
      }}
    >
      <div
        className="row border"
        style={{
          justifyContent: "center",
          textAlign: "center",
          margin: 20,
          maxWidth: 600,
        }}
      >
        <Button
          text="Living rooms"
          color={Color.primary}
          type={living}
          onPress={() => {
            setLiving(true);
            setBed(false);
            setKitchen(false);
            setType("livingRoom");
          }}
        />
        <Button
          text="Bedroom"
          color={Color.primary}
          type={bed}
          onPress={() => {
            setLiving(false);
            setBed(true);
            setKitchen(false);
            setType("bedRoom");
          }}
        />
        <Button
          text="Kitchen"
          color={Color.primary}
          type={kitchen}
          onPress={() => {
            setLiving(false);
            setBed(false);
            setKitchen(true);
            setType("kitchen");
          }}
        />
      </div>

      <div
        className="row"
        style={{
          backgroundColor: "#F8F8F6",
          justifyContent: "center",
          textAlign: "center",
        }}
      >
        <div
          className="border col-2 "
          style={{ minWidth: 300, margin: 15, textAlign: "center" }}
        >
          <h4>Filter by Themes</h4>
          <div
            style={{
              textAlign: "center",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button
              text="Modern"
              color={Color.primary}
              type={modern}
              onPress={() => {
                setModern(true);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Contemporary"
              color={Color.primary}
              type={contemporary}
              onPress={() => {
                setModern(false);
                setContemporary(true);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Minimalsitic"
              color={Color.primary}
              type={minimalsitic}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(true);
                setVintage(false);
                setEuropean(false);
              }}
            />
            <Button
              text="Vintage"
              color={Color.primary}
              type={vintage}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(true);
                setEuropean(false);
              }}
            />
            <Button
              text="European"
              color={Color.primary}
              type={european}
              onPress={() => {
                setModern(false);
                setContemporary(false);
                setMinimalsitic(false);
                setVintage(false);
                setEuropean(true);
              }}
            />
          </div>
        </div>
        <div className="col border" style={{ maxWidth: 1000, margin: 15 }}>
          {modern && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Modern"
              type={type}
            />
          )}
          {contemporary && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Contemporary"
              type={type}
            />
          )}
          {minimalsitic && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Minimalsitic"
              type={type}
            />
          )}
          {vintage && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="Vintage"
              type={type}
            />
          )}
          {european && (
            <Image
              living={living}
              bed={bed}
              kitchen={kitchen}
              theme="European"
              type={type}
            />
          )}

          {/* {image && (
            <SimpleImageSlider
              width={896}
              height={504}
              images={imgUrl}
              showBullets={true}
              showNavs={true}
            />
          )} */}
          {/* <SimpleImageSlider
            width={896}
            height={504}
            images={ImageData.filter((image) => image.type === "kitchen")}
            showBullets={true}
            showNavs={true}
          /> */}
        </div>
      </div>
    </div>
  );
};

export default Gallery;
