  const [name, setName] = useState("")
  const [home, homeSet] = useState("")

  useEffect(()=>{
    axios.get('http://localhost:3000/home').then(function (response) {
      homeSet(response.data)
    })
  }, [])

  async function postName(e) {
    e.preventDefault()
    try{
      await axios.post('http://localhost:3000/post_name', {
        name
      })
    } catch (error){
      console.log(error) 
    } 
  }

  return (
    <div className="App">

      {home}
    </div>
  );
}

export default App;
