import React from "react";
import styled from "styled-components";

export const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

const Container = styled.label`
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  .input {
    position: absolute;
    opacity: 0;
    cursor: pointer;
  }

  &:hover input ~ .CheckMark {
    background-color: #ccc;
  }
  input:checked ~ .CheckMark {
    background-color: #2196f3;
  }
`;
const CheckMark = styled.span`
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #eee;
  border-radius: 50%;
`;
export const ConstCostEstimator = () => {
  return (
    <div
      className="border"
      style={{
        marginTop: 50,
        backgroundColor: "#F8F8F6",
        width: 450,
        borderRadius: 5,
      }}
    >
      <h4 style={{ marginTop: 15 }}>Construction Cost Estimator</h4>
      <div>
        <Container>
          <input type="radio" id="plot" name="plotOrArea" />
          <CheckMark></CheckMark>
          Plot Size
        </Container>
        <Container>
          <input type="radio" id="plot" name="plotOrArea" />
          <CheckMark></CheckMark>
          Site Area
        </Container>

        {/* <input
          type="radio"
          id="plot"
          name="plotOrArea"
          style={{ margin: 10 }}
        />
        <label for="plot">Plot Size</label> */}
      </div>
    </div>
  );
};
