                {living &&
                  image.livingRoom.map((img, index) => (
                    <SmallImage src={img} key={index} />
                  ))}
                {bed &&
                  image.bedRoom.map((img, index) => (
                    <SmallImage src={img} key={index} />
                  ))}
                {kitchen &&
                  image.kitchenRoom.url.map((img, index) => (
                    <SmallImage
                      src={img}
                      key={index}
                      onClick={() => {
                        setImage(true);
                        setImgUrl(img);
                      }}
                    />
                  ))}
                <SimpleImageSlider
                  width={896}
                  height={504}
                  images={image.kitchenRoom}
                  showBullets={true}
                  showNavs={true}
                />
              </div>
            ))}
          </div>
