import React, { useState, Component } from "react";
import styled from "styled-components";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";
export const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

export class ConstCostEstimator extends React.Component {
  constructor() {
    super();

    this.state = {
      size: "Plot Size",
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({
      size: event.target.value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    alert(`You chose the ${this.state.size} pizza.`);
  }

  render() {
    return (
      <div
        className="border"
        style={{
          marginTop: 50,
          backgroundColor: "#F8F8F6",
          width: 450,
          borderRadius: 5,
        }}
      >
        <h4 style={{ marginTop: 15 }}>Construction Cost Estimator</h4>
        <div
          style={{ justifyContent: "center", textAlign: "center" }}
          onSubmit={this.handleSubmit}
        >
          <input
            className="form-check-input mt-1"
            type="radio"
            value="Plot Size"
            style={{ margin: 15 }}
            checked={this.state.size === "Plot Size"}
            onChange={this.handleChange}
          />
          <label>Plot Size</label>

          <input
            className="form-check-input mt-1"
            type="radio"
            value="Site Area"
            style={{ margin: 20 }}
            checked={this.state.size === "Site Area"}
            onChange={this.handleChange}
          />
          <label>Site Area</label>

          <br />
          <div
            className="block row"
            style={{ justifyContent: "center", textAlign: "center" }}
          >
            {this.state.size === "Plot Size" && (
              <select
                style={{
                  width: 200,
                  height: 30,
                  marginLeft: 15,
                  paddingLeft: 5,
                  marginRight: 5,
                }}
              >
                <option selected>Select</option>
                <option value="1">30 X 40</option>
                <option value="2">30 X 50</option>
                <option value="3">40 X 60</option>
              </select>
            )}
            {this.state.size === "Site Area" && (
              <label style={{ position: "relative" }}>
                Plot Size
                <input
                  type="number"
                  style={{
                    width: 110,
                    marginLeft: 15,
                    paddingLeft: 5,
                    marginRight: 5,
                  }}
                />
                <span style={{ position: "absolute", top: 1 }}>Sqft</span>
              </label>
            )}
            <label>Site Area</label>
            <div class="block">
              <label>Floor</label>
              <input type="text" />
            </div>
          </div>
        </div>
      </div>
    );
    return (
      <form onSubmit={this.handleSubmit}>
        <p className="title">Select a pizza size:</p>

        <ul>
          <li>
            <label>
              <input
                type="radio"
                value="small"
                checked={this.state.size === "small"}
                onChange={this.handleChange}
              />
              Small
            </label>
          </li>

          <li>
            <label>
              <input
                type="radio"
                value="medium"
                checked={this.state.size === "medium"}
                onChange={this.handleChange}
              />
              Medium
            </label>
          </li>

          <li>
            <label>
              <input
                type="radio"
                value="large"
                checked={this.state.size === "large"}
                onChange={this.handleChange}
              />
              Large
            </label>
          </li>
        </ul>

        <button type="submit" className="submit-button">
          Make your choice
        </button>
      </form>
    );
  }
}

