import React, { useState } from "react";
import { NavLink as Link } from "react-router-dom";
import styled from "styled-components";

const NavLink = styled(Link)`
  color: #fff;
  align-items: center;
  text-decoration: none;
  margin: 0 20px;
  padding-bottom: 10px;
  height: 100%;
  &.active {
    color: #15cdfc;
    font-weight: bold;
  }
`;

const Nav = styled.nav`
  background: #4a148c;
  width: 100%;
  padding: 0;
  margin: 0;
`;

const NavBar = () => {
  const [home, setHome] = useState(true);
  const [cons, setConst] = useState(false);
  const [interior, setInterior] = useState(false);
  const [renovation, setRenovation] = useState(false);
  const [about, setAbout] = useState(false);

  const MouseOver = (event) => {
    // event.target.style.borderBottom = "4px solid rgba(256, 256, 256, 0.5)";
    event.target.style.color = "#15cdfc";
  };
  const MouseOut = (event) => {
    event.target.style.borderBottom = "";
    event.target.style.color = "white";
  };
  const Home = () => {
    setHome(true);
    setConst(false);
  };
  const Const = () => {
    setConst(true);
    setHome(false);
  };
  const Interior = () => {
    setConst(false);
    setHome(false);
    setInterior(true);
  };
  return (
    <nav
      className="navbar navbar-expand-lg  sticky-top navbar-dark"
      style={{ width: "100%", background: "#4a148c" }}
    >
      <div className="container">
        <NavLink
          to="/"
          className="navbar-brand"
          onMouseOver={MouseOver}
          onMouseOut={MouseOut}
          onClick={Home}
        >
          <h1 style={{ color: home ? "#15cdfc" : "white" }}>Home</h1>
        </NavLink>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav">
            <li className="nav-item">
              <NavLink
                to="/construction"
                onMouseOver={MouseOver}
                onMouseOut={MouseOut}
                onClick={Const}
                decoration={cons ? "underline" : "none"}
              >
                Construction
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                to="/interior-design"
                onMouseOver={MouseOver}
                onMouseOut={MouseOut}
              >
                Interior Design
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                to="/renovation"
                onMouseOver={MouseOver}
                onMouseOut={MouseOut}
              >
                Renovation
              </NavLink>
            </li>
            <li className="nav-item">
              <NavLink
                to="/about"
                onMouseOver={MouseOver}
                onMouseOut={MouseOut}
              >
                About us
              </NavLink>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
