import React, { useEffect, useState } from 'react'
import axios from 'axios'
import styled from "styled-components";

import Color from "../constants/Color";

const Main = styled.div`
  height: 70vh;
  width: 100%;
  text-align: center;
  justify-content: space-around;
  color: white;
  background-image: url(${(props) => props.bg});
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  filter-brightness(50%);
`;
const Input = styled.input`
  width: 90%;
  height: 50px;
  padding: 5px;
  margin-top: 30px;
  border: 0;
  border-bottom: 1px solid;
  outline: none;
  font-size: 20px;
  background-color: #4a148c;
  color: white;
  border-radius: 4px;

  ::-webkit-inner-spin-button,
  ::-webkit-outer-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  &:-webkit-autofill,
  &:-webkit-autofill:hover,
  &:-webkit-autofill:focus,
  &:-webkit-autofill:active {
    -webkit-transition: "color 9999s ease-out, background-color 9999s ease-out";
    -webkit-transition-delay: 9999s;
  }
`;

const Form = styled.form`
  height: 450px;
  max-width: 400px;
  min-width: 400px;
  background-color: #4a148c;
  margin-top: 5%;
  opacity: 0.8;
  border-radius: 10px;

  @media screen and (max-width: 810px) {
    margin-top: 15%;
  }
`;
const Header = ({ title, text, bg }) => {
  const [name, setName] = useState("")
  const [home, homeSet] = useState("")

  // useEffect(()=>{
  //   axios.get('http://localhost:4000/home').then(function (response) {
  //     homeSet(response.data)
  //   })
  // }, [])

  async function postName(e) {
    e.preventDefault()
    //post 
    try{
      await axios.post('http://localhost:3000/userinfo', {
        name
      })
    } catch (error){
      console.log(error) 
    } 
  }


  // return (
  //   <div className="App">
  //     <form onSubmit={postName}>
  //       <input type='text' value={name} onChange={(e)=> setName(e.target.value)}/>
  //       <button type='submit'> send name </button>
  //     </form>
  //     {home}
  //   </div>
  // );

  return (
    <Main className="row" bg={bg}>
      <div className="col" style={{ maxWidth: "50%", minWidth: 400 }}>
        <p
          style={{
            fontSize: 45,
            fontWeight: "bold",
            marginTop: "10%",
            textShadow: `3px 3px 3px ${Color.primary}`,
          }}
        >
          {title}
        </p>
        <p
          style={{
            fontSize: 21,
            textShadow: `3px 3px 3px  ${Color.primary}`,
          }}
        >
          {text}
        </p>
      </div>

      <div className="App">
       <Form onSubmit={postName}>
        <input type='text' value={name} onChange={(e)=> setName(e.target.value)}/>
         <button type='submit'> send name </button>
       </Form>
       {home}
     </div>

      {/* <Form onSubmit={postName} >
        <Input value={name} onChange={(e)=> setName(e.target.value)} />
        <Input placeholder="Your contact number" type="number" />
        <Input placeholder="Your email" type="email" />
        <Input placeholder="Your city" type="text" />
        <button
          style={{ flex: 1, margin: 20 }}
          type="submit"
          class="btn btn-primary btn-lg btn-block"
        >
          Submit
        </button>
      </Form> */}
    </Main>
  );
};

export default Header;
