import React, { useState } from "react";
import styled from "styled-components";
import "bootstrap/dist/css/bootstrap.min.css";
import "./style.css";
export const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

export const ConstCostEstimator = () => {
  const [inputType, setInputType] = useState("Plot Size");

  const handleChange = (e) => {
    const { name, value } = e.target;

    console.log(value);
    setInputType(value);
  };
  const handleSubmit = (event) => {
    console.log(inputType);
    event.preventDefault();
  };
  return (
    <div
      className="border"
      style={{
        marginTop: 50,
        backgroundColor: "#F8F8F6",
        width: 450,
        borderRadius: 5,
      }}
    >
      <h4 style={{ marginTop: 15 }}>Construction Cost Estimator</h4>
      <div
        style={{ justifyContent: "center", textAlign: "center" }}
        onSubmit={handleSubmit}
      >
        <input
          checked
          type="radio"
          name="PlotOrArea"
          value="Plot Size"
          style={{ margin: 15 }}
          onChange={handleChange}
        />
        <label>Plot Size</label>

        <input
          type="radio"
          name="PlotOrArea"
          value="Site Area"
          style={{ margin: 20 }}
          onChange={handleChange}
        />
        <label>Site Area</label>
        {console.log(inputType)}
        <br />
        <div className="block">
          {inputType === "Plot Size" && <input />}
          {inputType === "Site Area" && (
            <label style={{ position: "relative" }}>
              Plot Size
              <input
                type="number"
                style={{
                  width: 110,
                  marginLeft: 15,
                  paddingLeft: 5,
                  marginRight: 5,
                }}
              />
              <span style={{ position: "absolute", top: 1 }}>Sqft</span>
            </label>
          )}
        </div>
      </div>
    </div>
  );
};
