import React, { useState } from "react";
import Button, { LargeButton } from "../../Button";
import Color from "../../../constants/Color";
import { SmallImage } from "./GalleryElements";

const Gallery = () => {
  const [living, setLiving] = useState(true);
  const [bed, setBed] = useState(false);
  const [kitchen, setKitchen] = useState(false);

  return (
    <div
      className="row"
      style={{ textAlign: "center", justifyContent: "center" }}
    >
      <div
        className="row border"
        style={{
          justifyContent: "center",
          textAlign: "center",
          margin: 20,
          maxWidth: 600,
        }}
      >
        <Button
          text="Living rooms"
          color={Color.primary}
          type={living}
          onPress={() => {
            setLiving(true);
            setBed(false);
            setKitchen(false);
          }}
        />
        <Button
          text="Bedroom"
          color={Color.primary}
          type={bed}
          onPress={() => {
            setLiving(false);
            setBed(true);
            setKitchen(false);
          }}
        />
        <Button
          text="Kitchen"
          color={Color.primary}
          type={kitchen}
          onPress={() => {
            setLiving(false);
            setBed(false);
            setKitchen(true);
          }}
        />
      </div>
      <h3>Modern</h3>
      <div
        className="row"
        style={{
          backgroundColor: "#F8F8F6",
          justifyContent: "center",
          textAlign: "center",
        }}
      >
        <div
          className="border col-2 "
          style={{ minWidth: 300, margin: 15, textAlign: "center" }}
        >
          <h4>Filter by Themes</h4>
          <div
            style={{
              textAlign: "center",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button
              text="Kitchen"
              color={Color.primary}
              type={kitchen}
              onPress={() => {
                setLiving(false);
                setBed(false);
                setKitchen(true);
              }}
            />
          </div>
        </div>
        <div className="col border" style={{ maxWidth: 1000, margin: 15 }}>
          <div>
            <SmallImage src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <SmallImage src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <SmallImage src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <SmallImage src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <SmallImage src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
            <SmallImage src="https://images.pexels.com/photos/1457842/pexels-photo-1457842.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Gallery;
