import React, { useState } from "react";
import styled from "styled-components";
import { FullHome, Bathroom, Kitchen, Floor } from "./Renovatoin-Type";
import Renovate from "./Renovate";
import Color from "../../../constants/Color";
import Button from "../../Button";

const cnst_bg = require("../../../assets/ren.jpeg").default;
const home1 = require("../../../assets/home2.png").default;
const homeWhite = require("../../../assets/home-white.png").default;
const bath1 = require("../../../assets/bath.png").default;
const bathWhite = require("../../../assets/bath-white.png").default;
const kitchen1 = require("../../../assets/kitchen.png").default;
const kitchenWhite = require("../../../assets/kitchen-white.png").default;
const floor1 = require("../../../assets/floor.png").default;
const floorWhite = require("../../../assets/floor-white.png").default;
const plus = require("../../../assets/add.png").default;
const minus = require("../../../assets/minus.png").default;

const Input = styled.input`
  width: 90%;
  height: 50px;
  padding: 5px;
  margin-top: 30px;
  border: 0;
  border-bottom: 1px solid;
  outline: none;
  font-size: 20px;
  background-color: #4a148c;
  color: white;
  border-radius: 4px;

  ::-webkit-inner-spin-button,
  ::-webkit-outer-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  &:-webkit-autofill,
  &:-webkit-autofill:hover,
  &:-webkit-autofill:focus,
  &:-webkit-autofill:active {
    -webkit-transition: "color 9999s ease-out, background-color 9999s ease-out";
    -webkit-transition-delay: 9999s;
  }
`;

const Form = styled.div`
  height: 450px;
  max-width: 400px;
  min-width: 400px;
  background-color: #4a148c;
  margin-top: 5%;
  opacity: 0.8;
  border-radius: 10px;

  @media screen and (max-width: 810px) {
    margin-top: 15%;
  }
`;

const Main = styled.div`
  height: 70vh;
  width: 100%;
  text-align: center;
  justify-content: space-around;
  color: white;
  background-image: url(${cnst_bg});
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
`;

const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

const FloorAddition = styled.div`
  width: 600px;
  height: 330px;
  background-color: white;
  margin: 10px;
  margin-top: 30px;
  text-align: center;
  border-radius: 10px;
  padding: 0;
`;

const Cost = styled.div`
  background-color: ${(props) => props.color};
  opacity: 0.6;
  color: white;
  border-top-right-radius: 10;
  border-top-left-radius: 10;
  font-weight: bold;
`;

const Renovation = () => {
  const [home, setHome] = useState(false);
  const [bath, setBath] = useState(false);
  const [kitchen, setKitchen] = useState(false);
  const [floor, setFloor] = useState(false);
  const [addFloor, setAddFloor] = useState(1);
  const [standard, setStandard] = useState(true);
  const [premium, setPremium] = useState(false);
  const [superPre, setSuperPre] = useState(false);
  const [luxury, setLuxury] = useState(false);

  return (
    <div className="row justify-content-center">
      <Main className="row">
        <div className="col" style={{ maxWidth: "50%", minWidth: 400 }}>
          <p
            style={{
              fontSize: 45,
              fontWeight: "550",
              marginTop: "10%",
              textShadow: "3px 3px 3px #4a148c",
            }}
          >
            Problem-Free Renovation with Qualified Experts
          </p>
          <p style={{ fontSize: 20, textShadow: "3px 3px 3px #4a148c" }}>
            100% Hassle-Free and managed by us!
          </p>
        </div>
        <Form className="col border-primary">
          <Input placeholder="Your name" type="text" />
          <Input placeholder="Your contact number" type="number" />
          <Input placeholder="Your email" type="email" />
          <Input placeholder="Your city" type="text" />
          <button
            style={{ flex: 1, margin: 22 }}
            type="submit"
            className="btn btn-primary btn-lg btn-block"
          >
            Submit
          </button>
        </Form>
      </Main>
      <Div className="row justify-content-center">
        <Renovate
          onPress={() => {
            setHome(!home);
            bath ? setBath(!bath) : setBath(bath);
            kitchen ? setKitchen(!kitchen) : setKitchen(kitchen);
            floor ? setFloor(!floor) : setFloor(floor);
          }}
          text="Full Home Renovation"
          color={home}
          icon={home ? homeWhite : home1}
        />
        <Renovate
          onPress={() => {
            setBath(!bath);
            home ? setHome(!home) : setHome(home);
            kitchen ? setKitchen(!kitchen) : setKitchen(kitchen);
            floor ? setFloor(!floor) : setFloor(floor);
          }}
          text="Bathroom Renovation"
          color={bath}
          icon={bath ? bathWhite : bath1}
        />
        <Renovate
          onPress={() => {
            setKitchen(!kitchen);
            home ? setHome(!home) : setHome(home);
            bath ? setBath(!bath) : setBath(bath);
            floor ? setFloor(!floor) : setFloor(floor);
          }}
          text="Kitchen Renovation"
          color={kitchen}
          icon={kitchen ? kitchenWhite : kitchen1}
        />
        <Renovate
          onPress={() => {
            setFloor(!floor);
            home ? setHome(!home) : setHome(home);
            bath ? setBath(!bath) : setBath(bath);
            kitchen ? setKitchen(!kitchen) : setKitchen(kitchen);
          }}
          text="Floor Tiling Work"
          color={floor}
          icon={floor ? floorWhite : floor1}
        />
      </Div>
      <div className="row justify-content-center container ">
        {home && <FullHome />}
        {bath && <Bathroom />}
        {kitchen && <Kitchen />}
        {floor && <Floor />}
      </div>
      <div
        className="row justify-content-center "
        style={{
          marginTop: 50,
          backgroundColor: "#F8F8F6",
          alignItems: "center",
        }}
      >
        <div
          className="border"
          style={{
            maxWidth: 480,
            marginTop: 30,
            textAlign: "center",
            fontSize: 30,
            fontWeight: "bold",
            backgroundColor: "white",
            borderRadius: 3,
            boxShadow:
              "rgba(0, 0, 0, 0.05) 0px 6px 24px 0px, #4a148c 0px 0px 0px 1px",
          }}
        >
          <p className="" style={{ fontWeight: "bold", marginTop: 10 }}>
            Floor Addition
          </p>
        </div>
        <FloorAddition className="border">
          <p
            style={{
              fontSize: 20,
              padding: 20,
              backgroundColor: Color.primary,
              width: "100%",
              color: "white",
              borderTopLeftRadius: 10,
              borderTopRightRadius: 10,
            }}
          >
            Cost Estimator
          </p>
          <div className="row" style={{ textAlign: "start", margin: 30 }}>
            <label style={{ fontWeight: "bold" }}>City</label>
            <input
              style={{ height: 30, marginLeft: 10 }}
              type="number"
              min="1"
              placeholder="Sqft"
            ></input>

            <label style={{ marginTop: 20, fontWeight: "bold" }}>
              New Floor Addition
            </label>
            <div
              style={{
                display: "flex",
              }}
            >
              <img
                onClick={() => {
                  addFloor > 1
                    ? setAddFloor(addFloor - 1)
                    : setAddFloor(addFloor);
                }}
                src={minus}
                style={{
                  height: 20,
                  width: 20,
                  cursor: "pointer",
                  marginTop: 10,
                }}
              />
              <p
                style={{
                  fontSize: 28,
                  marginLeft: 30,
                  marginRight: 30,
                  fontWeight: "bold",
                }}
              >
                {addFloor}
              </p>
              <img
                onClick={() => {
                  setAddFloor(addFloor + 1);
                }}
                src={plus}
                style={{
                  height: 20,
                  width: 20,
                  cursor: "pointer",
                  marginTop: 10,
                }}
              />
            </div>
            <div className="row justify-content-center ">
              <Button text="Calculate" color={Color.primary} />
            </div>
          </div>
        </FloorAddition>
        <div
          className="row "
          style={{
            justifyContent: "space-around",
            maxWidth: 1140,
            marginTop: 30,
          }}
        >
          <Button text="Standard" color="red" type={true} />
          <Button text="Premium" color="blue" type={true} />
          <Button text="Super Premium" color="green" type={true} />
          <Button text="Luxury" color="purple" type={true} />
        </div>
        <div
          style={{
            maxWidth: 1140,
            marginTop: 30,
            textAlign: "center",
          }}
        >
          <Cost color="blue">
            <p style={{ fontSize: 20, padding: 10, margin: 0 }}>
              TOTAL COST ₹{" "}
            </p>
            <p style={{ fontSize: 15, margin: 0 }}>Total Buildup Area </p>
            <p style={{ fontSize: 15, margin: 0, paddingBottom: 10 }}>
              Cost Calculated @ ₹{" "}
            </p>
          </Cost>
        </div>
      </div>
    </div>
  );
};

export default Renovation;

