import React, { useState } from "react";
import styled from "styled-components";
import { FullHome, Bathroom, Kitchen, Floor } from "./Renovatoin-Type";
import Renovate from "./Renovate";
import Color from "../../../constants/Color";

const cnst_bg = require("../../../assets/ren.jpeg").default;
const home1 = require("../../../assets/home2.png").default;
const homeWhite = require("../../../assets/home-white.png").default;
const bath1 = require("../../../assets/bath.png").default;
const bathWhite = require("../../../assets/bath-white.png").default;
const kitchen1 = require("../../../assets/kitchen.png").default;
const kitchenWhite = require("../../../assets/kitchen-white.png").default;
const floor1 = require("../../../assets/floor.png").default;
const floorWhite = require("../../../assets/floor-white.png").default;
const floorAdd = require("../../../assets/Floor_add.jpg").default;

const Input = styled.input`
  width: 90%;
  height: 50px;
  padding: 5px;
  margin-top: 30px;
  border: 0;
  border-bottom: 1px solid;
  outline: none;
  font-size: 20px;
  background-color: #4a148c;
  color: white;
  border-radius: 4px;

  ::-webkit-inner-spin-button,
  ::-webkit-outer-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }

  &:-webkit-autofill,
  &:-webkit-autofill:hover,
  &:-webkit-autofill:focus,
  &:-webkit-autofill:active {
    -webkit-transition: "color 9999s ease-out, background-color 9999s ease-out";
    -webkit-transition-delay: 9999s;
  }
`;

const Form = styled.div`
  height: 450px;
  max-width: 400px;
  min-width: 400px;
  background-color: #4a148c;
  margin-top: 5%;
  opacity: 0.8;
  border-radius: 10px;

  @media screen and (max-width: 810px) {
    margin-top: 15%;
  }
`;

const Main = styled.div`
  height: 70vh;
  width: 100%;
  text-align: center;
  justify-content: space-around;
  color: white;
  background-image: url(${cnst_bg});
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
`;

const Div = styled.div`
  text-align: center;
  margin-top: 40px;

  @media screen and (max-width: 810px) {
    margin-top: 250px;
  }
`;

const FloorAddition = styled.div`
  width: 500px;
  height: 300px;
  background-color: white;
  margin: 10px;
  margin-top: 30px;
  text-align: center;
  border-radius: 5px;
`;

const Renovation = () => {
  const [home, setHome] = useState(false);
  const [bath, setBath] = useState(false);
  const [kitchen, setKitchen] = useState(false);
  const [floor, setFloor] = useState(false);

  return (
    <div className="row justify-content-center w-100">
      <Main className="row">
        <div className="col" style={{ maxWidth: "50%", minWidth: 400 }}>
          <p
            style={{
              fontSize: 45,
              fontWeight: "550",
              marginTop: "10%",
              textShadow: "3px 3px 3px #4a148c",
            }}
          >
            Problem-Free Renovation with Qualified Experts
          </p>
          <p style={{ fontSize: 20, textShadow: "3px 3px 3px #4a148c" }}>
            100% Hassle-Free and managed by us!
          </p>
        </div>
        <Form className="col border-primary">
          <Input placeholder="Your name" type="text" />
          <Input placeholder="Your contact number" type="number" />
          <Input placeholder="Your email" type="email" />
          <Input placeholder="Your city" type="text" />
          <button
            style={{ flex: 1, margin: 22 }}
            type="submit"
            class="btn btn-primary btn-lg btn-block"
          >
            Submit
          </button>
        </Form>
      </Main>
      <Div className="row justify-content-center">
        <Renovate
          onPress={() => {
            setHome(!home);
            bath ? setBath(!bath) : setBath(bath);
            kitchen ? setKitchen(!kitchen) : setKitchen(kitchen);
            floor ? setFloor(!floor) : setFloor(floor);
          }}
          text="Full Home Renovation"
          color={home}
          icon={home ? homeWhite : home1}
        />
        <Renovate
          onPress={() => {
            setBath(!bath);
            home ? setHome(!home) : setHome(home);
            kitchen ? setKitchen(!kitchen) : setKitchen(kitchen);
            floor ? setFloor(!floor) : setFloor(floor);
          }}
          text="Bathroom Renovation"
          color={bath}
          icon={bath ? bathWhite : bath1}
        />
        <Renovate
          onPress={() => {
            setKitchen(!kitchen);
            home ? setHome(!home) : setHome(home);
            bath ? setBath(!bath) : setBath(bath);
            floor ? setFloor(!floor) : setFloor(floor);
          }}
          text="Kitchen Renovation"
          color={kitchen}
          icon={kitchen ? kitchenWhite : kitchen1}
        />
        <Renovate
          onPress={() => {
            setFloor(!floor);
            home ? setHome(!home) : setHome(home);
            bath ? setBath(!bath) : setBath(bath);
            kitchen ? setKitchen(!kitchen) : setKitchen(kitchen);
          }}
          text="Floor Tiling Work"
          color={floor}
          icon={floor ? floorWhite : floor1}
        />
      </Div>
      <div className="row justify-content-center container ">
        {home && <FullHome />}
        {bath && <Bathroom />}
        {kitchen && <Kitchen />}
        {floor && <Floor />}
      </div>
      <div
        className="row justify-content-center"
        style={{
          marginTop: 50,
          backgroundColor: "#F8F8F6",
          alignItems: "center",
        }}
      >
        <div
          style={{
            width: 500,
            textAlign: "center",
            fontSize: 30,
            fontWeight: "bold",
            margin: 10,
            marginTop: 30,
            backgroundColor: "white",
            borderRadius: 5,
          }}
        >
          Floor Addition
        </div>
        <FloorAddition className="border">
          <p
            style={{
              fontSize: 20,
              backgroundColor: Color.primary,
              width: "100%",
              color: "white",
              padding: 15,
            }}
          >
            Cost Estimator
          </p>
        </FloorAddition>
      </div>
    </div>
  );
};

export default Renovation;

// echo "# construction" >> README.md
