import React, { useState } from "react";
import Button from "../../Button";
import Color from "../../../constants/Color";
import { SmallImage, LargeImage } from "./GalleryElements";
import { ImageData } from "./imageData";
import SimpleImageSlider from "react-simple-image-slider";

const Gallery = () => {
  const [living, setLiving] = useState(true);
  const [bed, setBed] = useState(false);
  const [kitchen, setKitchen] = useState(false);
  const [image, setImage] = useState(true);
  const [imgUrl, setImgUrl] = useState("");

  return (
    <div
      className="row"
      style={{ textAlign: "center", justifyContent: "center" }}
    >
      <div
        className="row border"
        style={{
          justifyContent: "center",
          textAlign: "center",
          margin: 20,
          maxWidth: 600,
        }}
      >
        <Button
          text="Living rooms"
          color={Color.primary}
          type={living}
          onPress={() => {
            setLiving(true);
            setBed(false);
            setKitchen(false);
          }}
        />
        <Button
          text="Bedroom"
          color={Color.primary}
          type={bed}
          onPress={() => {
            setLiving(false);
            setBed(true);
            setKitchen(false);
          }}
        />
        <Button
          text="Kitchen"
          color={Color.primary}
          type={kitchen}
          onPress={() => {
            setLiving(false);
            setBed(false);
            setKitchen(true);
          }}
        />
      </div>

      <div
        className="row"
        style={{
          backgroundColor: "#F8F8F6",
          justifyContent: "center",
          textAlign: "center",
        }}
      >
        <div
          className="border col-2 "
          style={{ minWidth: 300, margin: 15, textAlign: "center" }}
        >
          <h4>Filter by Themes</h4>
          <div
            style={{
              textAlign: "center",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <Button text="Modern" color={Color.primary} />
          </div>
        </div>
        <div className="col border" style={{ maxWidth: 1000, margin: 15 }}>
          <h3>Modern</h3>
          {/* <div className="border"> */}
          {ImageData.map((image, i) => (
            <div className="row" key={i}>
              {living && image.type === "livingRoom" && (
                <SmallImage key={i} src={image.url} />
              )}
              {bed && image.type === "bedRoom" && (
                <SmallImage src={image.url} />
              )}
              {kitchen && image.type === "kitchen" && (
                // image.kitchenRoom.url.map((img, index) => (
                <div
                  className=" border"
                  style={{
                    width: "40%",
                    textAlign: "center",
                    justifyContent: "flex-end",
                  }}
                >
                  <SmallImage
                    src={image.url}
                    key={i}
                    onClick={() => {
                      // setImage(false);
                      // setImgUrl(img);
                    }}
                  />
                  <SmallImage
                    src={image.url}
                    key={i}
                    onClick={() => {
                      // setImage(false);
                      // setImgUrl(img);
                    }}
                  />
                </div>
              )}
            </div>
          ))}
          {/* </div> */}
          {/* {image && (
            <SimpleImageSlider
              width={896}
              height={504}
              images={imgUrl}
              showBullets={true}
              showNavs={true}
            />
          )} */}
        </div>
      </div>
    </div>
  );
};

