import React from "react";
import styled from "styled-components";

const Image = styled.div`
  width: 100%;
  height: 100%;
  background: url(${(props) => props.color});
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
  transition: all 1s;

  &:hover {
    transform: scale(1.1);
    filter: brightness(65%);
  }
`;
const Item = styled.a`
  /* background-color is value of props.color */
  // background: url(${(props) => props.color});
  width: 550px;
  height: 550px;
  margin: 20px;
  overflow: hidden;
  position: relative;
  cursor: pointer;
`;

const Text = styled.div`
  color: #ffffff;
  text-shadow: 3px 1px 1px #ea4492;
  font-size: 40px;
  font-weight: bold;
  position: absolute;
  bottom: 0;
  padding: 40px;
  text-decoration: none;
`;

const Offer = (props) => {
  return (
